import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./Products.css";

const Products = () => {
  const { category } = useParams();
  const navigate = useNavigate();
  const [subCategory, setSubCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [imageAttempts, setImageAttempts] = useState({});

  // This function turns "Exide Xplore (5Ah)" into "exide-xplore-5ah"
  const productNameToFilename = (productName) => {
    if (!productName) return "placeholder";
    return productName
      .toLowerCase()
      .replace(/\s+/g, '-')
      .replace(/[()]/g, '')
      .replace(/\//g, '-')
      .replace(/[^a-z0-9-]/g, '')
      .replace(/-+/g, '-')
      .replace(/^-|-$/g, '');
  };

  const handleImageError = (product, e) => {
    const productId = product._id;
    const extensions = ['jpg', 'jpeg', 'png', 'webp'];
    const currentAttempt = imageAttempts[productId] || 0;

    if (currentAttempt < extensions.length - 1) {
      // If .jpg fails, try .png, then .webp, etc.
      setImageAttempts(prev => ({ ...prev, [productId]: currentAttempt + 1 }));
    } else {
      // If all fail, show a generic placeholder
      e.target.onerror = null;
      e.target.src = "https://via.placeholder.com/300x250?text=No+Image+Found";
    }
  };

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await api.get("/products", { params: { category, subCategory } });
      setProducts(response.data);
    } catch (error) {
      console.error("Fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (subCategory) fetchProducts();
  }, [subCategory, category]);

  // UI rendering for subcategories (Vehicle/Home/etc)
  if (category === "battery" && !subCategory) {
    return (
      <div className="products-container">
        <div className="products-header">
          <button onClick={() => navigate("/inventory")} className="back-button">‚Üê Back</button>
          <h1>Batteries</h1>
        </div>
        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => setSubCategory("vehicle")}>
            <div className="subcategory-icon">üöó</div>
            <h3>Vehicle Batteries</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("home")}>
            <div className="subcategory-icon">üè†</div>
            <h3>Home Batteries</h3>
          </div>
        </div>
      </div>
    );
  }

  // Final Product Grid
  return (
    <div className="products-container">
      <div className="products-header">
        <button onClick={() => setSubCategory(null)} className="back-button">‚Üê Back</button>
        <h1>{subCategory?.replace('-', ' ')} Products</h1>
      </div>

      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <div className="products-grid">
          {products.map((product) => {
            const fileName = productNameToFilename(product.name);
            const extensions = ['jpg', 'jpeg', 'png', 'webp'];
            const currentExt = extensions[imageAttempts[product._id] || 0];
            const imageSrc = `/images/products/${fileName}.${currentExt}`;

            return (
              <div key={product._id} className="product-card">
                <div className="product-image-container">
                  <img 
                    src={imageSrc} 
                    alt={product.name}
                    className="product-image"
                    onError={(e) => handleImageError(product, e)}
                  />
                </div>
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p className="product-price">‚Çπ{product.price}</p>
                  <p className="product-stock">{product.stock} in stock</p>
                </div>
                <button className="add-to-cart-btn" disabled={product.stock === 0}>
                   Add to Cart
                </button>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default Products;
