import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./Products.css";

const Products = () => {
  const { category } = useParams();
  const navigate = useNavigate();
  const [subCategory, setSubCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // 1. Map for Two-Wheeler specific names seen in your sidebar
  const imageMap = {
    "Exide Xplore 12X2.5L-C (2.5Ah)": "exide-2-5ah.jpg",
    "Exide Xplore 12X5L-B (5Ah)": "exide-5l-b.jpg",
    "Exide Xplore 12X7-B (7Ah)": "exide-7-b.jpg",
    "Exide Xplore 12X9-B (9Ah)": "exide-9-b.jpg",
    "Exide Xplore 12X14L-A2 (14Ah)": "exide-14l-a2.jpg",
    "Exide Xplore XLTZ4A (4Ah)": "exide-xltz4a.jpg",
    "Exide Xplore XLTZ5A (5Ah)": "exide-xltz5a.jpg",
    "Exide Xplore XLTZ6 (5Ah)": "exide-xltz6.jpg",
    "Exide Xplore XLTZ7 (6Ah)": "exide-xltz7.jpg",
    "Exide Xplore XLTZ9 (8Ah)": "exide-xltz9.jpg",
    "Exide Xplore XLTZ14 (12Ah)": "exide-xltz14.jpg"
  };

  // 2. FIXED: Dynamically picks the folder based on the user's current selection
  const getProductImage = (product) => {
    // Priority 1: Use direct product image if available in DB
    if (product.image) return product.image;

    // Priority 2: Logic for specific subfolders
    const folder = subCategory; // e.g., "two-wheeler", "three-wheeler", "four-wheeler", "truck", "home"
    
    if (folder === "two-wheeler") {
      const fileName = imageMap[product.name];
      return fileName ? `/images/two-wheeler/${fileName}` : "/images/battery-placeholder.jpg";
    }

    // For other folders, it uses the imageName from your database (e.g., "h1.jpg", "t1.jpg")
    // This matches your sidebar structure exactly
    const fileName = product.imageName || "default.jpg";
    return `/images/${folder}/${fileName}`;
  };

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await api.get("/products", {
        params: { category, subCategory },
      });
      setProducts(response.data);
    } catch (error) {
      console.error("Error fetching products:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (subCategory) fetchProducts();
  }, [subCategory, category]);

  // --- NAVIGATION: Battery Selection ---
  if (category === "battery" && !subCategory) {
    return (
      <div className="products-container">
        <div className="products-header">
          <button onClick={() => navigate("/inventory")} className="back-button">‚Üê Back</button>
          <h1>Batteries</h1>
        </div>
        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => setSubCategory("vehicle")}>
            <div className="subcategory-icon">üöó</div>
            <h3>Vehicle Batteries</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("home")}>
            <div className="subcategory-icon">üè†</div>
            <h3>Home Batteries</h3>
          </div>
        </div>
      </div>
    );
  }

  // --- NAVIGATION: Vehicle Type Selection ---
  if (subCategory === "vehicle") {
    return (
      <div className="products-container">
        <div className="products-header">
          <button onClick={() => setSubCategory(null)} className="back-button">‚Üê Back</button>
          <h1>Vehicle Batteries</h1>
        </div>
        <div className="subcategories-grid">
          {["two-wheeler", "three-wheeler", "four-wheeler", "truck"].map((type) => (
            <div key={type} className="subcategory-card" onClick={() => setSubCategory(type)}>
              <div className="subcategory-icon">
                {type === "two-wheeler" ? "üèçÔ∏è" : type === "three-wheeler" ? "üõ∫" : type === "four-wheeler" ? "üöô" : "üöõ"}
              </div>
              <h3>{type.replace("-", " ").toUpperCase()}</h3>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // --- NAVIGATION: UPS Selection ---
  if (category === "ups" && !subCategory) {
    return (
      <div className="products-container">
        <div className="products-header">
          <button onClick={() => navigate("/inventory")} className="back-button">‚Üê Back</button>
          <h1>UPS Systems</h1>
        </div>
        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => setSubCategory("ups-type1")}>
             <div className="subcategory-icon">üí°</div>
             <h3>Standard UPS</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("ups-type2")}>
             <div className="subcategory-icon">‚ö°</div>
             <h3>Advanced UPS</h3>
          </div>
        </div>
      </div>
    );
  }

  // --- MAIN GRID: Renders all cards with images from their folders ---
  return (
    <div className="products-container">
      <div className="products-header">
        <button 
          onClick={() => setSubCategory(category === 'battery' ? (subCategory === 'home' ? null : 'vehicle') : null)} 
          className="back-button"
        >
          ‚Üê Back
        </button>
        <h1>{subCategory ? subCategory.toUpperCase().replace("-", " ") : "PRODUCTS"}</h1>
      </div>

      {loading ? (
        <div className="loading">Loading products...</div>
      ) : (
        <div className="products-grid">
          {products.map((product) => (
            <div key={product._id} className="product-card">
              <div className="product-image-container">
                <img 
                  src={getProductImage(product)} 
                  alt={product.name}
                  className="product-image"
                  onError={(e) => { e.target.src = "/images/battery-placeholder.jpg"; }}
                />
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <div className="product-footer">
                  <p className="product-price">‚Çπ{product.price}</p>
                  <p className="product-stock">{product.stock} in stock</p>
                </div>
              </div>
              <button 
                className="add-to-cart-btn" 
                disabled={product.stock === 0}
                onClick={() => {
                  const cart = JSON.parse(localStorage.getItem("cart") || "[]");
                  const existingItem = cart.find(item => item.productId === product._id);
                  if (existingItem) {
                    existingItem.quantity += 1;
                  } else {
                    cart.push({
                      productId: product._id,
                      productName: product.name,
                      price: product.price,
                      quantity: 1,
                    });
                  }
                  localStorage.setItem("cart", JSON.stringify(cart));
                  alert("Product added to cart!");
                }}
              >
                {product.stock === 0 ? "Out of Stock" : "Add to Cart"}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Products;
