import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./Products.css";

const Products = () => {
  const { category } = useParams();
  const navigate = useNavigate();

  const [subCategory, setSubCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  /* -----------------------------------
     IMAGE MAPPING (BASED ON YOUR FOLDER)
  ------------------------------------ */
  const getProductImage = (product) => {
    if (product.image) return product.image;

    if (category === "battery") {
      if (product.subCategory === "two-wheeler")
        return "/images/products/battery-two-wheeler.jpg";
      if (product.subCategory === "three-wheeler")
        return "/images/products/battery-three-wheeler.jpg";
      if (product.subCategory === "four-wheeler")
        return "/images/products/battery-four-wheeler.jpg";
      if (product.subCategory === "truck-series")
        return "/images/products/battery-truck.jpg";
      if (product.subCategory === "home")
        return "/images/products/battery-home.jpg";
    }

    if (category === "ups") {
      if (product.subCategory === "ups-type1")
        return "/images/ups-type1.jpg";
      if (product.subCategory === "ups-type2")
        return "/images/ups-type2.jpg";
      if (product.subCategory === "ups-type3")
        return "/images/ups-type3.jpg";
    }

    return "/images/logo.svg";
  };

  /* -------------------------
     FETCH PRODUCTS
  -------------------------- */
  useEffect(() => {
    if (!subCategory) return;

    const fetchProducts = async () => {
      try {
        setLoading(true);
        const res = await api.get("/products", {
          params: { category, subCategory },
        });
        setProducts(res.data);
      } catch (err) {
        console.error("Error fetching products", err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [category, subCategory]);

  /* -------------------------
     BATTERY CATEGORY FLOW
  -------------------------- */
  if (category === "battery" && !subCategory) {
    return (
      <div className="products-container">
        <button className="back-button" onClick={() => navigate("/inventory")}>
          ‚Üê Back
        </button>
        <h1>Batteries</h1>

        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => setSubCategory("vehicle")}>
            üöó <h3>Vehicle Batteries</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("home")}>
            üè† <h3>Home Batteries</h3>
          </div>
        </div>
      </div>
    );
  }

  if (category === "battery" && subCategory === "vehicle") {
    return (
      <div className="products-container">
        <button className="back-button" onClick={() => setSubCategory(null)}>
          ‚Üê Back
        </button>
        <h1>Vehicle Batteries</h1>

        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => setSubCategory("two-wheeler")}>
            üèçÔ∏è <h3>Two Wheeler</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("three-wheeler")}>
            üõ∫ <h3>Three Wheeler</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("four-wheeler")}>
            üöô <h3>Four Wheeler</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("truck-series")}>
            üöõ <h3>Truck Series</h3>
          </div>
        </div>
      </div>
    );
  }

  /* -------------------------
     UPS CATEGORY FLOW
  -------------------------- */
  if (category === "ups" && !subCategory) {
    return (
      <div className="products-container">
        <button className="back-button" onClick={() => navigate("/inventory")}>
          ‚Üê Back
        </button>
        <h1>UPS Systems</h1>

        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => setSubCategory("ups-type1")}>
            üí° <h3>Standard UPS</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("ups-type2")}>
            ‚ö° <h3>Advanced UPS</h3>
          </div>
          <div className="subcategory-card" onClick={() => setSubCategory("ups-type3")}>
            üîå <h3>Premium UPS</h3>
          </div>
        </div>
      </div>
    );
  }

  /* -------------------------
     PRODUCTS LIST
  -------------------------- */
  return (
    <div className="products-container">
      <button className="back-button" onClick={() => setSubCategory(null)}>
        ‚Üê Back
      </button>

      {loading ? (
        <p>Loading products...</p>
      ) : (
        <div className="products-grid">
          {products.map((product) => (
            <div className="product-card" key={product._id}>
              <img
                src={getProductImage(product)}
                alt={product.name}
                className="product-image"
              />

              <h3>{product.name}</h3>
              <p>{product.description}</p>

              <div className="product-footer">
                <span>‚Çπ{product.price}</span>
                <span>{product.stock > 0 ? "In Stock" : "Out of Stock"}</span>
              </div>

              <button
                disabled={product.stock === 0}
                onClick={() => {
                  const cart = JSON.parse(localStorage.getItem("cart") || "[]");
                  const existing = cart.find(i => i.productId === product._id);

                  if (existing) existing.quantity += 1;
                  else cart.push({
                    productId: product._id,
                    productName: product.name,
                    price: product.price,
                    quantity: 1,
                  });

                  localStorage.setItem("cart", JSON.stringify(cart));
                  alert("Added to cart");
                }}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Products;
