import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./Products.css";

const Products = () => {
  const { category } = useParams();
  const navigate = useNavigate();
  const [subCategory, setSubCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Helper function to convert product name to image filename
  const productNameToFilename = (productName) => {
    return productName
      .toLowerCase()
      .replace(/\s+/g, '-')           // Replace spaces with hyphens
      .replace(/[()]/g, '')           // Remove parentheses
      .replace(/\//g, '-')            // Replace slashes with hyphens
      .replace(/[^a-z0-9-]/g, '')     // Remove special characters
      .replace(/-+/g, '-')            // Replace multiple hyphens with single
      .replace(/^-|-$/g, '');         // Remove leading/trailing hyphens
  };

  // Function to get image based on product name or category
  const getDefaultImage = (product) => {
    const subCat = product.subCategory;
    
    // Priority 1: Use product.image field if available
    if (product.image) return product.image;
    
    // Priority 2: Try product-specific image based on product name
    const productFilename = productNameToFilename(product.name);
    const productSpecificImage = `/images/products/${productFilename}.jpg`;
    
    // Priority 3: Fall back to category-based images
    const categoryImages = {
      'battery': {
        'two-wheeler': '/images/battery-two-wheeler.jpg',
        'three-wheeler': '/images/battery-three-wheeler.jpg',
        'four-wheeler': '/images/battery-four-wheeler.jpg',
        'truck-series': '/images/battery-truck.jpg',
        'home': '/images/battery-home.jpg',
      },
      'ups': {
        'ups-type1': '/images/ups-type1.jpg',
        'ups-type2': '/images/ups-type2.jpg',
        'ups-type3': '/images/ups-type3.jpg',
      }
    };
    
    // Return product-specific image (will try this first)
    // If it fails, onError will fall back to category image
    return productSpecificImage;
  };

  // Get fallback category image
  const getCategoryFallbackImage = (product) => {
    const subCat = product.subCategory;
    
    if (category === 'battery') {
      if (subCat === 'two-wheeler') return '/images/battery-two-wheeler.jpg';
      if (subCat === 'three-wheeler') return '/images/battery-three-wheeler.jpg';
      if (subCat === 'four-wheeler') return '/images/battery-four-wheeler.jpg';
      if (subCat === 'truck-series') return '/images/battery-truck.jpg';
      if (subCat === 'home') return '/images/battery-home.jpg';
      return '/images/battery-default.jpg';
    }
    
    if (category === 'ups') {
      if (subCat === 'ups-type1') return '/images/ups-type1.jpg';
      if (subCat === 'ups-type2') return '/images/ups-type2.jpg';
      if (subCat === 'ups-type3') return '/images/ups-type3.jpg';
      return '/images/ups-default.jpg';
    }
    
    return '/images/product-placeholder.jpg';
  };

  useEffect(() => {
    if (subCategory) {
      fetchProducts();
    }
  }, [subCategory, category]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await api.get("/products", {
        params: { category, subCategory },
      });
      setProducts(response.data);
    } catch (error) {
      console.error("Error fetching products:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubCategoryClick = (subCat) => {
    setSubCategory(subCat);
  };

  if (category === "battery") {
    if (!subCategory) {
      return (
        <div className="products-container">
          <div className="products-header">
            <button onClick={() => navigate("/inventory")} className="back-button">
              ‚Üê Back
            </button>
            <h1>Batteries</h1>
            <p>Select a battery type</p>
          </div>

          <div className="subcategories-grid">
            <div
              className="subcategory-card"
              onClick={() => handleSubCategoryClick("vehicle")}
            >
              <div className="subcategory-icon">üöó</div>
              <h3>Vehicle Batteries</h3>
              <p>For all types of vehicles</p>
            </div>
            <div
              className="subcategory-card"
              onClick={() => handleSubCategoryClick("home")}
            >
              <div className="subcategory-icon">üè†</div>
              <h3>Home Batteries</h3>
              <p>Inverters & Home use</p>
            </div>
          </div>
        </div>
      );
    }

    if (subCategory === "vehicle") {
      return (
        <div className="products-container">
          <div className="products-header">
            <button onClick={() => setSubCategory(null)} className="back-button">
              ‚Üê Back
            </button>
            <h1>Vehicle Batteries</h1>
            <p>Select vehicle type</p>
          </div>

          <div className="subcategories-grid">
            <div
              className="subcategory-card"
              onClick={() => handleSubCategoryClick("two-wheeler")}
            >
              <div className="subcategory-icon">üèçÔ∏è</div>
              <h3>Two Wheeler</h3>
              <p>Bikes & Scooters</p>
            </div>
            <div
              className="subcategory-card"
              onClick={() => handleSubCategoryClick("three-wheeler")}
            >
              <div className="subcategory-icon">üõ∫</div>
              <h3>Three Wheeler</h3>
              <p>Auto Rickshaws</p>
            </div>
            <div
              className="subcategory-card"
              onClick={() => handleSubCategoryClick("four-wheeler")}
            >
              <div className="subcategory-icon">üöô</div>
              <h3>Four Wheeler</h3>
              <p>Cars & SUVs</p>
            </div>
            <div
              className="subcategory-card"
              onClick={() => handleSubCategoryClick("truck-series")}
            >
              <div className="subcategory-icon">üöõ</div>
              <h3>Truck Series</h3>
              <p>Heavy Vehicles</p>
            </div>
          </div>
        </div>
      );
    }
  }

  if (category === "ups" && !subCategory) {
    return (
      <div className="products-container">
        <div className="products-header">
          <button onClick={() => navigate("/inventory")} className="back-button">
            ‚Üê Back
          </button>
          <h1>UPS Systems</h1>
          <p>Select a UPS type</p>
        </div>

        <div className="subcategories-grid">
          <div
            className="subcategory-card ups-card-1"
            onClick={() => handleSubCategoryClick("ups-type1")}
          >
            <div className="subcategory-icon">üí°</div>
            <h3>Standard UPS</h3>
            <p>600VA - 1000VA | Home & Office</p>
          </div>
          <div
            className="subcategory-card ups-card-2"
            onClick={() => handleSubCategoryClick("ups-type2")}
          >
            <div className="subcategory-icon">‚ö°</div>
            <h3>Advanced UPS</h3>
            <p>1500VA - 2000VA | Business Grade</p>
          </div>
          <div
            className="subcategory-card ups-card-3"
            onClick={() => handleSubCategoryClick("ups-type3")}
          >
            <div className="subcategory-icon">üîå</div>
            <h3>Premium UPS</h3>
            <p>3000VA+ | Industrial & Server</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="products-container">
      <div className="products-header">
        <button
          onClick={() => setSubCategory(null)}
          className="back-button"
        >
          ‚Üê Back
        </button>
        <h1>Products</h1>
      </div>

      {loading ? (
        <div className="loading">Loading products...</div>
      ) : products.length === 0 ? (
        <div className="no-products">
          <p>No products found in this category.</p>
          <button onClick={() => setSubCategory(null)}>Go Back</button>
        </div>
      ) : (
        <div className="products-grid">
          {products.map((product) => (
            <div key={product._id} className="product-card">
              <div className="product-image-container">
                <img 
                  src={product.image || getDefaultImage(product)} 
                  alt={product.name}
                  className="product-image"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = getDefaultImage(product);
                  }}
                />
                {product.stock === 0 && (
                  <div className="out-of-stock-badge">Out of Stock</div>
                )}
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <p className="product-description">{product.description}</p>
                <div className="product-footer">
                  <p className="product-price">‚Çπ{product.price}</p>
                  <p className="product-stock">
                    {product.stock > 0 ? `${product.stock} in stock` : "Out of Stock"}
                  </p>
                </div>
              </div>
              <button
                className="add-to-cart-btn"
                onClick={() => {
                  const cart = JSON.parse(localStorage.getItem("cart") || "[]");
                  const existingItem = cart.find(
                    (item) => item.productId === product._id
                  );
                  if (existingItem) {
                    existingItem.quantity += 1;
                  } else {
                    cart.push({
                      productId: product._id,
                      productName: product.name,
                      price: product.price,
                      quantity: 1,
                    });
                  }
                  localStorage.setItem("cart", JSON.stringify(cart));
                  alert("Product added to cart!");
                }}
                disabled={product.stock === 0}
              >
                {product.stock === 0 ? "Out of Stock" : "Add to Cart"}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Products;
