import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./Products.css";

const Products = () => {
  const { category } = useParams();
  const navigate = useNavigate();
  const [subCategory, setSubCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Map product name to the exact image filename in your folder
  const getProductImage = (product) => {
    // Replace special characters that break URL with %20
    const encodedName = encodeURIComponent(product.name);
    return `/images/products/${encodedName}.jpg`;
  };

  const handleImageError = (e) => {
    e.target.onerror = null; // prevent infinite loop
    // fallback placeholder
    e.target.src = "/images/products/placeholder.jpg"; // create a placeholder in same folder
  };

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await api.get("/products", {
        params: { category, subCategory },
      });
      setProducts(response.data);
    } catch (error) {
      console.error("Error fetching products:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (subCategory) fetchProducts();
  }, [subCategory, category]);

  const handleSubCategoryClick = (subCat) => setSubCategory(subCat);

  // Subcategory pages
  if (category === "battery" && !subCategory) {
    return (
      <div className="products-container">
        <div className="products-header">
          <button onClick={() => navigate("/inventory")} className="back-button">‚Üê Back</button>
          <h1>Batteries</h1>
          <p>Select a battery type</p>
        </div>
        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => handleSubCategoryClick("vehicle")}>
            <div className="subcategory-icon">üöó</div>
            <h3>Vehicle Batteries</h3>
          </div>
          <div className="subcategory-card" onClick={() => handleSubCategoryClick("home")}>
            <div className="subcategory-icon">üè†</div>
            <h3>Home Batteries</h3>
          </div>
        </div>
      </div>
    );
  }

  if (category === "ups" && !subCategory) {
    return (
      <div className="products-container">
        <div className="products-header">
          <button onClick={() => navigate("/inventory")} className="back-button">‚Üê Back</button>
          <h1>UPS Systems</h1>
          <p>Select a UPS type</p>
        </div>
        <div className="subcategories-grid">
          <div className="subcategory-card" onClick={() => handleSubCategoryClick("ups-type1")}>
            <div className="subcategory-icon">üí°</div>
            <h3>Standard UPS</h3>
          </div>
          <div className="subcategory-card" onClick={() => handleSubCategoryClick("ups-type2")}>
            <div className="subcategory-icon">‚ö°</div>
            <h3>Advanced UPS</h3>
          </div>
          <div className="subcategory-card" onClick={() => handleSubCategoryClick("ups-type3")}>
            <div className="subcategory-icon">üîå</div>
            <h3>Premium UPS</h3>
          </div>
        </div>
      </div>
    );
  }

  // Main products grid
  return (
    <div className="products-container">
      <div className="products-header">
        <button onClick={() => setSubCategory(null)} className="back-button">‚Üê Back</button>
        <h1>Products</h1>
      </div>

      {loading ? (
        <div className="loading">Loading products...</div>
      ) : products.length === 0 ? (
        <div className="no-products">
          <p>No products found.</p>
          <button onClick={() => setSubCategory(null)}>Go Back</button>
        </div>
      ) : (
        <div className="products-grid">
          {products.map((product) => (
            <div key={product._id} className="product-card">
              <div className="product-image-container">
                <img
                  src={getProductImage(product)}
                  alt={product.name}
                  className="product-image"
                  onError={handleImageError}
                  loading="lazy"
                />
                {product.stock === 0 && <div className="out-of-stock-badge">Out of Stock</div>}
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <div className="product-footer">
                  <p className="product-price">‚Çπ{product.price}</p>
                  <p>{product.stock > 0 ? `${product.stock} in stock` : "Out of Stock"}</p>
                </div>
              </div>
              <button
                className="add-to-cart-btn"
                onClick={() => {
                  const cart = JSON.parse(localStorage.getItem("cart") || "[]");
                  const existing = cart.find((item) => item.productId === product._id);
                  if (existing) existing.quantity += 1;
                  else cart.push({ productId: product._id, productName: product.name, price: product.price, quantity: 1 });
                  localStorage.setItem("cart", JSON.stringify(cart));
                  alert("Product added to cart!");
                }}
                disabled={product.stock === 0}
              >
                {product.stock === 0 ? "Out of Stock" : "Add to Cart"}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Products;
