import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import "./Cart.css";

const Cart = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [deliveryAddress, setDeliveryAddress] = useState({
    street: "",
    city: "",
    state: "",
    pincode: "",
  });
  const [location, setLocation] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart") || "[]");
    setCart(savedCart);
    getLocation();
  }, []);

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Error getting location:", error);
        }
      );
    }
  };

  const updateQuantity = (productId, change) => {
    const updatedCart = cart.map((item) => {
      if (item.productId === productId) {
        const newQuantity = item.quantity + change;
        if (newQuantity <= 0) return null;
        return { ...item, quantity: newQuantity };
      }
      return item;
    }).filter(Boolean);

    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const removeItem = (productId) => {
    const updatedCart = cart.filter((item) => item.productId !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const totalAmount = cart.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleCheckout = async () => {
    if (!user) {
      navigate("/login");
      return;
    }

    if (cart.length === 0) {
      alert("Your cart is empty!");
      return;
    }

    setLoading(true);
    try {
      // Update user location
      if (location) {
        await api.put("/auth/location", location);
      }

      const orderData = {
        items: cart,
        deliveryAddress,
        deliveryLocation: location,
      };

      const response = await api.post("/orders", orderData);
      localStorage.removeItem("cart");
      setCart([]);
      alert("Order placed successfully! Order ID: " + response.data._id);
      navigate("/orders");
    } catch (error) {
      alert("Error placing order: " + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  if (cart.length === 0) {
    return (
      <div className="cart-container">
        <div className="empty-cart">
          <h2>Your cart is empty</h2>
          <button onClick={() => navigate("/inventory")} className="shop-button">
            Continue Shopping
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>Shopping Cart</h1>
      <div className="cart-content">
        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.productId} className="cart-item">
              <div className="item-info">
                <h3>{item.productName}</h3>
                <p>‚Çπ{item.price} each</p>
              </div>
              <div className="item-controls">
                <button onClick={() => updateQuantity(item.productId, -1)}>
                  -
                </button>
                <span>{item.quantity}</span>
                <button onClick={() => updateQuantity(item.productId, 1)}>
                  +
                </button>
                <button
                  className="remove-btn"
                  onClick={() => removeItem(item.productId)}
                >
                  Remove
                </button>
              </div>
              <div className="item-total">
                ‚Çπ{item.price * item.quantity}
              </div>
            </div>
          ))}
        </div>

        <div className="checkout-section">
          <h2>Checkout</h2>
          <div className="form-group">
            <label>Street Address</label>
            <input
              type="text"
              value={deliveryAddress.street}
              onChange={(e) =>
                setDeliveryAddress({ ...deliveryAddress, street: e.target.value })
              }
            />
          </div>
          <div className="form-group">
            <label>City</label>
            <input
              type="text"
              value={deliveryAddress.city}
              onChange={(e) =>
                setDeliveryAddress({ ...deliveryAddress, city: e.target.value })
              }
            />
          </div>
          <div className="form-group">
            <label>State</label>
            <input
              type="text"
              value={deliveryAddress.state}
              onChange={(e) =>
                setDeliveryAddress({ ...deliveryAddress, state: e.target.value })
              }
            />
          </div>
          <div className="form-group">
            <label>Pincode</label>
            <input
              type="text"
              value={deliveryAddress.pincode}
              onChange={(e) =>
                setDeliveryAddress({
                  ...deliveryAddress,
                  pincode: e.target.value,
                })
              }
            />
          </div>
          {location && (
            <p className="location-status">üìç Location captured</p>
          )}
          <div className="total-amount">
            <h3>Total: ‚Çπ{totalAmount}</h3>
          </div>
          <button
            className="checkout-button"
            onClick={handleCheckout}
            disabled={loading}
          >
            {loading ? "Processing..." : "Place Order"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
