import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import "./Cart.css";

const Cart = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [deliveryAddress, setDeliveryAddress] = useState({
    street: "",
    city: "",
    state: "",
    pincode: "",
  });
  const [location, setLocation] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart") || "[]");
    setCart(savedCart);
    getLocation();
  }, []);

  const getLocation = () => {
    if (!navigator.geolocation) {
      alert("Location is not supported by your browser.");
      return;
    }
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const coords = {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        };
        setLocation(coords);
        try {
          const res = await fetch(
            `https://nominatim.openstreetmap.org/reverse?format=json&lat=${coords.latitude}&lon=${coords.longitude}&addressdetails=1`,
            { headers: { "Accept-Language": "en" } }
          );
          const data = await res.json();
          const a = data.address || {};
          setDeliveryAddress((prev) => ({
            ...prev,
            street: [a.house_number, a.road, a.suburb].filter(Boolean).join(", ") || prev.street,
            city: a.city || a.town || a.village || a.county || prev.city,
            state: a.state || prev.state,
            pincode: a.postcode || prev.pincode,
          }));
        } catch (e) {
          console.error("Reverse geocode failed:", e);
        }
      },
      (error) => {
        console.error("Error getting location:", error);
        alert("Could not get your location. Please enter address manually.");
      }
    );
  };

  const updateQuantity = (productId, change) => {
    const updatedCart = cart.map((item) => {
      if (item.productId === productId) {
        const newQuantity = item.quantity + change;
        if (newQuantity <= 0) return null;
        return { ...item, quantity: newQuantity };
      }
      return item;
    }).filter(Boolean);

    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const removeItem = (productId) => {
    const updatedCart = cart.filter((item) => item.productId !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const totalAmount = cart.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleCheckout = async () => {
    if (!user) {
      navigate("/login");
      return;
    }

    if (cart.length === 0) {
      alert("Your cart is empty!");
      return;
    }

    const hasAddress = deliveryAddress.street?.trim() || deliveryAddress.city?.trim() || deliveryAddress.pincode?.trim();
    if (!hasAddress && !location) {
      alert("Please add your delivery address or use \"Use my location\" so we can deliver to you.");
      return;
    }

    setLoading(true);
    try {
      // Update user location
      if (location) {
        await api.put("/auth/location", location);
      }

      const orderData = {
        items: cart,
        deliveryAddress,
        deliveryLocation: location,
      };

      const response = await api.post("/orders", orderData);
      localStorage.removeItem("cart");
      setCart([]);
      alert("Order placed successfully! Order ID: " + response.data._id);
      navigate("/orders");
    } catch (error) {
      const errorMessage = error.response?.data?.message || error.message;
      alert("Error placing order: " + errorMessage);
      
      // If user not found, redirect to login
      if (error.response?.data?.code === "USER_NOT_FOUND") {
        localStorage.removeItem("token");
        setTimeout(() => {
          navigate("/login");
        }, 2000);
      }
    } finally {
      setLoading(false);
    }
  };

  if (cart.length === 0) {
    return (
      <div className="cart-container">
        <div className="empty-cart">
          <h2>Your cart is empty</h2>
          <button onClick={() => navigate("/inventory")} className="shop-button">
            Continue Shopping
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h1>Shopping Cart</h1>
      <div className="cart-content">
        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.productId} className="cart-item">
              <div className="item-info">
                <h3>{item.productName}</h3>
                <p>‚Çπ{item.price} each</p>
              </div>
              <div className="item-controls">
                <button onClick={() => updateQuantity(item.productId, -1)}>
                  -
                </button>
                <span>{item.quantity}</span>
                <button onClick={() => updateQuantity(item.productId, 1)}>
                  +
                </button>
                <button
                  className="remove-btn"
                  onClick={() => removeItem(item.productId)}
                >
                  Remove
                </button>
              </div>
              <div className="item-total">
                ‚Çπ{item.price * item.quantity}
              </div>
            </div>
          ))}
        </div>

        <div className="checkout-section">
          <h2>Checkout</h2>
          <div className="form-group">
            <label>Street Address</label>
            <input
              type="text"
              value={deliveryAddress.street}
              onChange={(e) =>
                setDeliveryAddress({ ...deliveryAddress, street: e.target.value })
              }
            />
          </div>
          <div className="form-group">
            <label>City</label>
            <input
              type="text"
              value={deliveryAddress.city}
              onChange={(e) =>
                setDeliveryAddress({ ...deliveryAddress, city: e.target.value })
              }
            />
          </div>
          <div className="form-group">
            <label>State</label>
            <input
              type="text"
              value={deliveryAddress.state}
              onChange={(e) =>
                setDeliveryAddress({ ...deliveryAddress, state: e.target.value })
              }
            />
          </div>
          <div className="form-group">
            <label>Pincode</label>
            <input
              type="text"
              value={deliveryAddress.pincode}
              onChange={(e) =>
                setDeliveryAddress({
                  ...deliveryAddress,
                  pincode: e.target.value,
                })
              }
            />
          </div>
          <div className="location-actions">
            <button type="button" className="use-location-btn" onClick={getLocation}>
              üìç Use my location
            </button>
            {location && (
              <p className="location-status">üìç Location captured ‚Ä¢ Address filled from your location</p>
            )}
          </div>
          
          {/* Bill Breakdown */}
          <div className="bill-section">
            <h3>Bill Details</h3>
            <div className="bill-items">
              {cart.map((item) => (
                <div key={item.productId} className="bill-item">
                  <div className="bill-item-name">{item.productName}</div>
                  <div className="bill-item-details">
                    <span>Qty: {item.quantity}</span>
                    <span>√ó ‚Çπ{item.price}</span>
                    <span className="bill-item-total">= ‚Çπ{item.price * item.quantity}</span>
                  </div>
                </div>
              ))}
            </div>
            <div className="bill-total">
              <div className="bill-total-row">
                <span>Subtotal:</span>
                <span>‚Çπ{totalAmount}</span>
              </div>
              <div className="bill-total-row">
                <span>Delivery Charges:</span>
                <span>‚Çπ0</span>
              </div>
              <div className="bill-total-row final-total">
                <span><strong>Total Amount:</strong></span>
                <span><strong>‚Çπ{totalAmount}</strong></span>
              </div>
            </div>
          </div>

          <button
            className="checkout-button"
            onClick={handleCheckout}
            disabled={loading}
          >
            {loading ? "Processing..." : "Place Order & Generate Bill"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
