import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import "./Admin.css";

const Admin = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    if (!user || user.role !== "admin") {
      navigate("/");
      return;
    }
    fetchOrders();
    const interval = setInterval(fetchOrders, 5000); // Refresh every 5 seconds
    return () => clearInterval(interval);
  }, [user, navigate]);

  const fetchOrders = async () => {
    try {
      const response = await api.get("/orders");
      setOrders(response.data);
    } catch (error) {
      console.error("Error fetching orders:", error);
    } finally {
      setLoading(false);
    }
  };

  const updateOrderStatus = async (orderId, status) => {
    try {
      await api.put(`/orders/${orderId}/status`, { status });
      fetchOrders();
      setSelectedOrder(null);
    } catch (error) {
      alert("Error updating order status: " + (error.response?.data?.message || error.message));
    }
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: "#ffc107",
      accepted: "#17a2b8",
      "ready-to-deliver": "#28a745",
      "out-for-delivery": "#007bff",
      delivered: "#6c757d",
      cancelled: "#dc3545",
    };
    return colors[status] || "#666";
  };

  const getNextStatus = (currentStatus) => {
    const statusFlow = {
      pending: "accepted",
      accepted: "ready-to-deliver",
      "ready-to-deliver": "out-for-delivery",
      "out-for-delivery": "delivered",
    };
    return statusFlow[currentStatus];
  };

  if (loading) {
    return (
      <div className="admin-container">
        <div className="loading">Loading orders...</div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <h1>Admin Dashboard</h1>
      <div className="orders-stats">
        <div className="stat-card">
          <h3>Total Orders</h3>
          <p>{orders.length}</p>
        </div>
        <div className="stat-card">
          <h3>Pending</h3>
          <p>{orders.filter((o) => o.status === "pending").length}</p>
        </div>
        <div className="stat-card">
          <h3>Ready to Deliver</h3>
          <p>{orders.filter((o) => o.status === "ready-to-deliver").length}</p>
        </div>
      </div>

      <div className="orders-list">
        {orders.map((order) => (
          <div key={order._id} className="order-card">
            <div className="order-header">
              <div>
                <h3>Order #{order._id.slice(-6).toUpperCase()}</h3>
                <p className="order-customer">
                  Customer: {order.customerName} | {order.customerPhone} | {order.customerEmail}
                </p>
                <p className="order-date">
                  {new Date(order.createdAt).toLocaleString()}
                </p>
              </div>
              <div
                className="order-status"
                style={{ backgroundColor: getStatusColor(order.status) }}
              >
                {order.status
                  .split("-")
                  .map((w) => w.charAt(0).toUpperCase() + w.slice(1))
                  .join(" ")}
              </div>
            </div>

            <div className="order-items">
              <h4>Items:</h4>
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <span>{item.productName}</span>
                  <span>
                    {item.quantity} x ₹{item.price} = ₹
                    {item.quantity * item.price}
                  </span>
                </div>
              ))}
            </div>

            <div className="order-footer">
              <div className="order-total">
                <strong>Total: ₹{order.totalAmount}</strong>
              </div>
              {order.deliveryAddress && (
                <div className="order-address">
                  <strong>Delivery Address:</strong>{" "}
                  {Object.values(order.deliveryAddress).filter(Boolean).join(", ")}
                </div>
              )}
              {order.deliveryLocation && (
                <div className="order-location">
                  <strong>Location:</strong>{" "}
                  <a
                    href={`https://www.google.com/maps?q=${order.deliveryLocation.latitude},${order.deliveryLocation.longitude}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    View on Map
                  </a>
                </div>
              )}
            </div>

            <div className="order-actions">
              {getNextStatus(order.status) && (
                <button
                  className="status-button"
                  onClick={() =>
                    updateOrderStatus(order._id, getNextStatus(order.status))
                  }
                >
                  Mark as {getNextStatus(order.status)
                    .split("-")
                    .map((w) => w.charAt(0).toUpperCase() + w.slice(1))
                    .join(" ")}
                </button>
              )}
              <button
                className="view-button"
                onClick={() => setSelectedOrder(order)}
              >
                View Details
              </button>
            </div>
          </div>
        ))}
      </div>

      {selectedOrder && (
        <div className="modal-overlay" onClick={() => setSelectedOrder(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Order Details</h2>
            <div className="modal-body">
              <p><strong>Order ID:</strong> {selectedOrder._id}</p>
              <p><strong>Customer:</strong> {selectedOrder.customerName}</p>
              <p><strong>Phone:</strong> {selectedOrder.customerPhone}</p>
              <p><strong>Email:</strong> {selectedOrder.customerEmail}</p>
              <p><strong>Status:</strong> {selectedOrder.status}</p>
              <p><strong>Total:</strong> ₹{selectedOrder.totalAmount}</p>
              {selectedOrder.deliveryLocation && (
                <p>
                  <strong>Location:</strong>{" "}
                  <a
                    href={`https://www.google.com/maps?q=${selectedOrder.deliveryLocation.latitude},${selectedOrder.deliveryLocation.longitude}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Open in Google Maps
                  </a>
                </p>
              )}
            </div>
            <button
              className="close-button"
              onClick={() => setSelectedOrder(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Admin;
